/* global require */
"use strict";

var fluid = fluid || require("infusion"),
    gpii = fluid.registerNamespace("gpii"),
    $ = fluid.registerNamespace("jQuery");

fluid.registerNamespace("gpii.contextManager.utils");
fluid.registerNamespace("gpii.contextManager.transforms");

gpii.contextManager.utils.transformTypeLookup = {
    "http://registry.gpii.net/conditions/inRange": "gpii.contextManager.transforms.inRange",
    "http://registry.gpii.net/conditions/timeInRange": "gpii.contextManager.transforms.timeInRange"

};

// Transforms required for condition evaluation:
fluid.defaults("gpii.contextManager.transforms.inRange", {
    gradeNames: "fluid.standardTransformFunction"
});

gpii.contextManager.transforms.inRange = function (value, transformSpec) {
    return (transformSpec.min && transformSpec.min > value) ||
        (transformSpec.max && transformSpec.max < value) ? false : true;
};

// Transforms required for condition evaluation:
fluid.defaults("gpii.contextManager.transforms.timeInRange", {
    gradeNames: "fluid.standardTransformFunction"
});


gpii.contextManager.transforms.timeInRange = function (value, transformSpec) {
    var isEarlier = gpii.contextManager.transforms.timeInRange.isEarlier;
    var currentTime = {
        hours: (new Date(value)).getHours(),
        minutes: (new Date(value)).getMinutes()
    };
    var fromTime = gpii.contextManager.transforms.timeInRange.timeParser(transformSpec.from);
    var toTime = gpii.contextManager.transforms.timeInRange.timeParser(transformSpec.to);

    if (isEarlier(toTime, fromTime)) { //if time wraps
        return (isEarlier(fromTime, currentTime) || isEarlier(currentTime, toTime));
    } else {
        return (isEarlier(fromTime, currentTime) && isEarlier(currentTime, toTime));
    }
};

gpii.contextManager.transforms.timeInRange.timeParser = function (time) {
    time = time.split(":");
    return {
        hours: parseInt(time[0], 10),
        minutes: parseInt(time[1], 10)
    };
};

// checks whether 'first' is earlier than 'second' - returns true if so
gpii.contextManager.transforms.timeInRange.isEarlier = function (first, second) {
    return (60 * (first.hours - second.hours) + first.minutes - second.minutes) < 0;
};

gpii.contextManager.utils.findActiveContexts = function (currentContext, matchData) {
    var activeContexts = {};
    activeContexts["indices"] = [];
    activeContexts["saliences"] = [];
    // do nothing if there is no NP set yet
    fluid.each(matchData.inferredConfiguration, function (content, contextId) {
        if (content.conditions === undefined) {
            return;
        }
        // massage conditions to be proper transform specs:
        var condArray = [];
        fluid.each(content.conditions, function (condition) {
            var entry = fluid.copy(condition);
            entry.type = gpii.contextManager.utils.transformTypeLookup[condition.type];
            if (entry.type === undefined) {
                console.log("ERROR: Failed to find the condition type: " + condition.type + " - Ignoring that conditional block");
                condArray = false;
                return;
            }
            condArray.push({
                transform: entry
            });
        });
        var result = fluid.model.transformWithRules(currentContext, { evaluations: condArray });
        // if we only have 'true' entries after evaluation
        if (result.evaluations && $.inArray(false, result.evaluations) === -1) {
            // TODO  (done->needs checking)Take priorities into account
            if (activeContexts["indices"].length < 1){
                activeContexts["indices"].push(contextId);
                activeContexts["saliences"].push(content["salience"]);
                
            } else {
                var pos = 0;
                while ((activeContexts["saliences"][pos])&&(activeContexts["saliences"][pos] > content["salience"])){
                    pos++;
                }
                activeContexts["indices"].splice(pos,0,contextId);
                activeContexts["saliences"].splice(pos,0,content["salience"]);
                
                
            };
            
            // prepend the entry
            // activeContexts.unshift(contextId);
        }
    });
    // if there are no feasible contexts, then choose gpii-default as the active one
    // note that this breaks Kasper's UtilitiesTests as they always expect to have gpii-default active
    if (activeContexts["indices"].length < 1){
        activeContexts["indices"] = [ "gpii-default" ];
    }
    fluid.log("Active contexts calculated to be: " + activeContexts["indices"]);
    return activeContexts["indices"];
};




gpii.contextManager.utils.expandContext = function (userToken, matchData){
    var newMatchData = fluid.copy(matchData);
    var i = 0;
    // TODO: (done->needs checking) add priorities if they are not already added
    fluid.each( newMatchData.inferredConfiguration , function ( inferredConfiguration ) {
        // fluid.log("inferredConfiguration is "+ JSON.stringify (inferredConfiguration, null, " "));
        
        if (inferredConfiguration.salience === undefined){
        // then it is user-stated, should be ge 1024
            inferredConfiguration.salience = 1024+i;
            i++;
            
        }
    
    });
    // TODO: (done->needs checking) WiP add contexts
    if (userToken === "sammy"){
        var ctxObj = fluid.copy(matchData.inferredConfiguration["gpii-default"]);
        var ctxSettings = ctxObj["applications"]["com.microsoft.windows.magnifier"]["settings"];
        var ctxConditions = [];
        var ctxConditionsObj = {};
        
        // ctxObj["name"] = "VERY HiGH luminance";
        ctxSettings["http://registry.gpii.net/common/invertColours"] = false;
        ctxSettings["http://registry.gpii.net/common/magnification"] = 5.0;
        ctxSettings["http://registry.gpii.net/common/backgroundColor"] = "white";
        ctxSettings["http://registry.gpii.net/common/foregroundColor"] = "black";
        
        ctxConditionsObj["type"] = "http://registry.gpii.net/conditions/inRange";
        ctxConditionsObj["min"] = 2500;
        //commented to test priorities ctxConditionsObj["max"] = 50000;
        ctxConditionsObj["inputPath"] = "http://registry\\.gpii\\.net/environment/luminance";
        ctxConditions.push(ctxConditionsObj);
        
        ctxObj["conditions"] = ctxConditions;
        ctxObj["salience"] = 512;
        newMatchData.inferredConfiguration.lightHighest = ctxObj;
    }
    
    return newMatchData;
};



