(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.defaults("kettle.requests.request.handler.deviceGet", {
        gradeNames: ["fluid.littleComponent", "autoInit"],
        invokers: {
            handle: {
                funcName: "gpii.handleDeviceGet",
                args: ["{requestProxy}", "{deviceReporter}.installedSolutionsDataSource", "{deviceReporter}.platformReporter"]
            }
        }
    });

    gpii.handleDeviceGet = function (requestProxy, installedSolutionsDataSource, platformReporter) {
        installedSolutionsDataSource.get(null, function onSuccess(solutions) {
            requestProxy.events.onSuccess.fire({
                solutions: solutions,
                OS: platformReporter.reportPlatform()
            });
        });
    };

})();
