(function () {

    "use strict";

    var fluid = require("infusion"),
        os = require("os"),
        kettle = fluid.require("kettle", require);

    kettle.requests.request.handler.deviceGet = function (requestProxy, installedSolutionsDataSource) {
        installedSolutionsDataSource.get(null, function (solutions) {
            requestProxy.events.onSuccess.fire({
                solutions: solutions,
                OS: {
                    //TODO: need to report more details - windowmanager, etc.
                    id: os.platform(),
                    version: os.release().replace("_", "-") // TODO: Need a better strategy--Node semver fails horribly in the face of the benign underscore (eg. x86_64).
                }
            });
        });
    };

})();