(function () {

    "use strict";

    var fluid = require("infusion"),
        os = require("os"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "deviceGet", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.deviceGet",
                    args: ["{requestProxy}", "{deviceReporter}.installedSolutionsDataSource"]
                }
            }
        }
    });

    gpii.requests.request.handler.deviceGet = function (requestProxy, installedSolutionsDataSource) {
        installedSolutionsDataSource.get(function (solutions) {
            if (!solutions) {
                requestProxy.events.onError.fire();
                return;
            }
            requestProxy.events.onSuccess.fire({
                solutions: solutions,
                OS: {
                    //TODO: need to report more details - windowmanager, etc.
                    id: os.platform(), 
                    version: os.release().replace("_", "-") // TODO: Need a better strategy--Node semver fails horribly in the face of the benign underscore (eg. x86_64).
                }
            });
        });
    };

})();