(function () {

    "use strict";

    var fluid = require("infusion"),
        path = require("path"),
        gpii = fluid.registerNamespace("gpii");

    fluid.require("kettle", require);
    fluid.require("./UserLogin.js", require);
    fluid.require("./Settings.js", require);
    fluid.require("./UserLogout.js", require);
    fluid.require("./UserSave.js", require);
    fluid.require("./GetToken.js", require);
    fluid.require("./UserUpdate.js", require);

    fluid.require("lifecycleManager", require);

    fluid.defaults("gpii.flowManager", {
        gradeNames: ["kettle.app", "autoInit"],
        urls: {
            preferences: "",
            deviceReporter: "",
            matchMaker: "",
            solutionsReporter: "",
            lifecycleManagerServer: ""
        },
        root: path.join(__dirname, ".."),
        components: {
            preferencesDataSource: {
                type: "kettle.dataSource.URL",
                options: {
                    gradeNames: ["kettle.dataSource.simpleCallbackWrapper"],
                    url: "{gpii.flowManager}.options.urls.preferences",
                    termMap: {
                        token: "%token"
                    }
                }
            },
            deviceReporterDataSource: {
                type: "kettle.dataSource.URL",
                options: {
                    gradeNames: ["kettle.dataSource.simpleCallbackWrapper"],
                    url: "{gpii.flowManager}.options.urls.deviceReporter"
                }
            },
            matchMakerDataSource: {
                type: "kettle.dataSource.URL",
                options: {
                    gradeNames: ["kettle.dataSource.simpleCallbackWrapper"],
                    url: "{gpii.flowManager}.options.urls.matchMaker",
                    writable: true
                }
            },
            lifecycleManagerServerDataSource: {
                type: "kettle.dataSource.URL",
                options: {
                    gradeNames: ["kettle.dataSource.simpleCallbackWrapper"],
                    url: "{gpii.flowManager}.options.urls.lifecycleManagerServer",
                    writable: true,
                    termMap: {
                        operation: "%operation",
                        token: "%token"
                    }
                }
            }
        },
        handlers: {
            userLogin: {
                route: "/user/:token/login",
                type: "get"
            },
            userLogout: {
                route: "/user/:token/logout",
                type: "get"
            },
            getToken: {
                route: "/token",
                type: "get"
            }
        }
    });

    fluid.defaults("gpii.flowManager.dev", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        distributeOptions: {
            source: "{that}.options.urlExpanderGradeNames",
            target: "{that urlExpander}.options.gradeNames"
        },
        urlExpanderGradeNames: ["kettle.urlExpander.development"]
    });

    fluid.defaults("gpii.flowManager.save", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        handlers: {
            userSave: {
                route: "/save/:token?",
                type: "post"
            }
        },
        distributeOptions: {
            source: "{that}.options.writable",
            target: "{that > preferencesDataSource}.options.writable"
        },
        writable: true
    });

    fluid.defaults("gpii.flowManager.io", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        handlers: {
            userUpdate: {
                route: "/update",
                type: "io",
                useSession: "existing"
            },
            userLogin: {
                useSession: "create"
            },
            userLogout: {
                useSession: "existing"
            },
            userSave: {
                useSession: "existing"
            },
            getToken: {
                useSession: "existing"
            }
        }
    });

    fluid.defaults("gpii.flowManager.io.server", {
        gradeNames: ["fluid.littleComponent", "autoInit"],
        distributeOptions: {
            source: "{that}.options.validateToken",
            target: "{that > sessionManager}.options.invokers.validate"
        },
        validateToken: {
            funcName: "gpii.flowManager.io.validateToken"
        }
    });

    // TODO: This invoker configuration system will need to be replaced with
    // what will be available with FLUID-5241.
    gpii.flowManager.io.validateToken = function (request) {
        return request.session && request.session.session.token;
    };

    fluid.defaults("gpii.flowManager.cloudBased", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        mergePolicy: {
            handleUnsupported: "noexpand"
        },
        components: {
            deviceReporterDataSource: {
                type: "fluid.emptySubcomponent"
            },
            lifecycleManagerServerDataSource: {
                type: "fluid.emptySubcomponent"
            }
        },
        urls: {
            deviceReporter: "",
        },
        handlers: {
            settings: {
                route: "/:token/settings/:device",
                type: "get"
            },
            userLogin: null,
            userLogout: null
        }
    });

    fluid.defaults("kettle.gradeLinkageRecord.requestSession", {
        gradeNames: ["autoInit", "fluid.gradeLinkageRecord"],
        contextGrades: ["kettle.use.session.request"],
        resultGrades: ["gpii.flowManager.requestSession"]
    });

    fluid.defaults("gpii.flowManager.requestSession", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        distributeOptions: {
            source: "{that}.options.handlerGradeNames",
            target: "{that handler}.options.gradeNames"
        },
        handlerGradeNames: ["gpii.flowManager.handlerSession"]
    })

})();