 /*global require*/

(function () {

    "use strict";
    
    var fluid = require("infusion")
    var gpii = fluid.registerNamespace("gpii");
    fluid.registerNamespace("gpii.request.flowManager");
    
    fluid.defaults("kettle.requests.request.handler.getToken", {
        gradeNames: ["gpii.request.flowManager.sessionAware", "autoInit"],
        invokers: {
            handle: {
                funcName: "gpii.request.flowManager.onGetToken",
                args: ["{that}", "{requestProxy}.events"
                ]
            }
        }
    });

    gpii.request.flowManager.onGetToken = function (that, events) {
        that.withSession(function (session, tokens) {
            var token = tokens.join(",");
            fluid.log("Reporting currently logged in user(s) as: " + token);
            events.onSuccess.fire(token);
        }, "No user currently logged into the system");
    };

})();
