(function () {

    "use strict";

    var fluid = require("infusion"),
        uuid = require("node-uuid"),
        kettle = fluid.require("kettle", require);

    kettle.requests.request.handler.userLogout = function (requestProxy, token, lifecycleManager) {
        // TODO: clarify semantics of multiple active start/stop cycles.
        lifecycleManager.stop({userToken: token}, function (response) {
            fluid.log("Launch manager returned: ", response);
            requestProxy.events.onSuccess.fire("User with token " + token + " was successfully logged out.");
        });
    };

})();