(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.registerNamespace("gpii.request.flowManager");

    fluid.defaults("kettle.requests.request.handler.userLogout", {
        gradeNames: ["fluid.littleComponent", "autoInit"],
        invokers: {
            handle: {
                funcName: "gpii.request.flowManager.onUserLogout",
                args: ["{requestProxy}", "{request}.req.params.userToken", "{flowManager}.lifecycleManager"],
                dynamic: true
            }
        }
    });

    gpii.request.flowManager.onUserLogout = function (requestProxy, userToken, lifecycleManager) {
        // TODO: clarify semantics of multiple active start/stop cycles.
        lifecycleManager.stop({
            userToken: userToken
        }, function onSuccess(response) {
            fluid.log("Lifecycle manager returned: ", response);
            requestProxy.events.onSuccess.fire("User with token " + userToken +
                " was successfully logged out.");
        });
    };

})();
