(function () {
    "use strict";

    var fluid = require("infusion"),
        request = fluid.registerNamespace("gpii.request.flowManager");

    fluid.defaults("kettle.requests.request.handler.userSavePost", {
        gradeNames: ["fluid.littleComponent", "autoInit"],
        invokers: {
            handle: {
                funcName: "gpii.request.flowManager.onUserSave",
                args: [
                    "{flowManager}.preferencesDataSource",
                    "{request}.req.params.token",
                    "{request}.req.body",
                    "{requestProxy}.events.onSuccess"
                ],
                dynamic: true
            }
        }
    });

    fluid.defaults("kettle.requests.request.handler.userSavePut", {
        gradeNames: ["fluid.littleComponent", "autoInit"],
        invokers: {
            handle: {
                funcName: "gpii.request.flowManager.onUserSave",
                args: [
                    "{flowManager}.preferencesDataSource",
                    "{request}.req.params.token",
                    "{request}.req.body",
                    "{requestProxy}.events.onSuccess"
                ],
                dynamic: true
            }
        }
    });

    request.onUserSave = function (preferencesDataSource, token, preferences, event) {
        preferencesDataSource.options.writeMethod = (token !== undefined && token !== "") ?
            "PUT" : "POST";

        preferencesDataSource.set({
            token: token || ""
        }, preferences, request.logAndNotify("Saved user preferences: ", event));
    };

})();
