(function () {

    "use strict";

    var fluid = require("infusion"),
        request = fluid.registerNamespace("gpii.request.flowManager");

    fluid.defaults("kettle.requests.request.handler.userSave", {
        gradeNames: ["fluid.littleComponent", "autoInit"],
        invokers: {
            handle: {
                funcName: "gpii.request.flowManager.onUserSave",
                args: [
                    "{flowManager}.preferencesDataSource",
                    "{request}.req.params.token",
                    "{request}.req.body",
                    "{requestProxy}.events.onSuccess"
                ],
                dynamic: true
            }
        }
    });

    request.onUserSave = function (preferencesDataSource, token, preferences, event) {
        // TODO: Slight faultiness here - we must encode a "missing value" as the empty string, since this value
        // is transmitted directly as a URL request parameter to the upstream DataSource. We need to make sure that
        // round-tripping via a DataSource's directModel (via "termMap" etc.) is not lossy
        token = token || "";
        var directModel = {
            token: token
        };
        preferencesDataSource.set(directModel, preferences, request.logAndNotify("Saved user preferences: ", event));
    };

})();
