// Set up mock ajax responses
gpii.tests.untrustedSettingsDataSource.setUpNock = function (config) {
    var cloudMock = nock(gpii.tests.untrustedSettingsDataSource.hostname);

    // log nock matches
    cloudMock.log(console.log);

    // mock POST requests to "/access_token"
    cloudMock.post(config.accessToken.url, config.accessToken.data)
        .reply(config.accessToken.status, config.accessToken.response);

    // mock GET requests to "/untrusted-settings"
    cloudMock.get(config.untrustedSettingsGet.url)
        .matchHeader(config.untrustedSettingsGet.authHeader.header, config.untrustedSettingsGet.authHeader.value)
        .reply(config.untrustedSettingsGet.status, config.untrustedSettingsGet.response);

    // mock PUT requests to "/untrusted-settings"
    cloudMock.put(config.untrustedSettingsPut.url, gpii.tests.untrustedSettingsDataSource.preferencesToUpdate)
        .matchHeader(config.untrustedSettingsPut.authHeader.header, config.untrustedSettingsPut.authHeader.value)
        .reply(config.untrustedSettingsPut.status, config.untrustedSettingsPut.response);
};

