/*global require, console*/

(function () {

    var fluid = require("infusion"),
        jqUnit = fluid.require("jqUnit"),
        path = require("path"),
        http = require("http"),
        fs = require("fs"),
        gpii = fluid.registerNamespace("gpii");

    fluid.require("flowManager", require);
    fluid.registerNamespace("gpii.test");

    gpii.test.handleResponse = function (testDescription, response, expectedResponse) {
        var responseData = "";

        jqUnit.assertTrue("Callback for " + testDescription + " is called", true);

        response.on("data", function (chunk) {
            responseData += chunk;
        });

        response.on("end", function () {
            jqUnit.assertTrue("Response from the server is received", true);
            jqUnit.assertEquals("Response data is expected", expectedResponse, responseData);
            jqUnit.start();
        });
    };

    jqUnit.asyncTest("Flow Manager handles proper login requests", function () {
        jqUnit.expect(3);
        var flowManager = gpii.flowManager();
        var expectedResponse = "User was successfully logged in.";
        http.get({
            host: "localhost",
            port: 8081,
            path: "/user/andrei/login"
        }, function (response) {
            response.on("end", function () {
                flowManager.server.close();
            });
            gpii.test.handleResponse("logging user in", response, expectedResponse);
        });
    });

    jqUnit.asyncTest("Flow Manager handles proper logout requests", function () {
        jqUnit.expect(3);
        var flowManager = gpii.flowManager();
        var expectedResponse = "User with token andrei was successfully logged out.";
        http.get({
            host: "localhost",
            port: 8081,
            path: "/user/andrei/logout"
        }, function (response) {
            response.on("end", function () {
                flowManager.server.close();
            });
            gpii.test.handleResponse("logging user out", response, expectedResponse);
        });
    });

    jqUnit.test("Flow Manager and its server are initialized", function () {
        jqUnit.expect(2);
        var flowManager = gpii.flowManager();
        jqUnit.assertValue("Flow manager is initialized", flowManager);
        jqUnit.assertValue("Flow manager's server is initialized", flowManager.server);
        flowManager.server.close();
    });
}());