/*global require, console*/

(function () {

    var fluid = require("infusion"),
        jqUnit = fluid.require("jqUnit"),
        path = require("path"),
        http = require("http"),
        fs = require("fs"),
        gpii = fluid.registerNamespace("gpii");

    fluid.require("flowManager", require);
    fluid.registerNamespace("gpii.test");

    jqUnit.test("Flow Manager and its server are initialized", function () {
        jqUnit.expect(2);
        var flowManager = gpii.flowManager();
        jqUnit.assertValue("Flow manager is initialized", flowManager);
        jqUnit.assertValue("Flow manager's server is initialized", flowManager.server);
        flowManager.server.close();
    });
}());