"use strict";

var os = require("os"),
    fs = require("fs"),
    path = require("path"),
    crypto = require("crypto");

var fluid = require("gpii-universal");
var kettle = fluid.require("kettle");
kettle.loadTestingSupport();
var jqUnit = fluid.require("node-jqunit");
var gpii = fluid.registerNamespace("gpii");

fluid.registerNamespace("gpii.tests.iod.installer");

require("../index.js");

gpii.tests.iod.teardowns = [];

jqUnit.module("gpii.tests.iod.installer", {
    teardown: function () {
        while (gpii.tests.iod.teardowns.length) {
            gpii.tests.iod.teardowns.pop()();
        }
    }
});

fluid.defaults("gpii.tests.iod", {
    gradeNames: [ "gpii.iod" ],
    components: {
        "testInstaller": {
            type: "gpii.tests.iod.installer"
        },
        "packages": {
            type: "gpii.iod.packages",
            options: {
                components: {
                    "packageDataSource": {
                        type: "kettle.dataSource.file",
                        options: {
                            path: __dirname + "/testPackages/%packageName.json"
                        }
                    }
                }
            }
        }
    }
});

fluid.defaults("gpii.tests.iod.installer", {
    gradeNames: ["fluid.component", "gpii.iod.packageInstaller"],

    invokers: {
        initialise: "gpii.tests.iod.installer.stage({that}, initialise)",
        downloadPackage: "gpii.tests.iod.installer.stage({that}, downloadPackage)",
        checkPackage: "gpii.tests.iod.installer.stage({that}, checkPackage)",
        prepareInstall: "gpii.tests.iod.installer.stage({that}, prepareInstall)",
        installPackage: "gpii.tests.iod.installer.stage({that}, installPackage)",
        cleanup: "gpii.tests.iod.installer.stage({that}, cleanup)",
        uninstallPackage: "gpii.tests.iod.installer.stage({that}, uninstallPackage)"
    },

    packageTypes: "testPackageType1"
});

gpii.tests.iod.installer.stage = function (that, stage) {
    that.stages.push(stage);
};

// Test startInstaller starts the installation pipe-line.
jqUnit.test("test getInstaller", function () {

    var iod = gpii.tests.iod();
    var installer = iod.getInstaller("testPackageType1");

    installer.stages = [];

    installer.startInstaller({}).then(function () {
        var expect = [
            "initialise",
            "downloadPackage",
            "checkPackage",
            "prepareInstall",
            "installPackage",
            "cleanup"
        ];

        jqUnit.assertDeepEq("All stages of the installation should be called in order.", expect, installer.stages);

    }, jqUnit.fail);
});


jqUnit.asyncTest("test https download", function () {

    gpii.tests.iod.installer.downloadTests = fluid.freezeRecursive([
        {
            url: "https://raw.githubusercontent.com/GPII/universal/108be0f5f0377eaec9100c1926647e7550efc2ea/gpii.js",
            expect: "8cb82683c931e15995b2573fda41c41eaacab59e"
        },
        {
            url: "https://gpii-test.invalid",
            expect: "reject"
        },
        // Certificate problems
        {
            url: "https://badssl.com",
            expect: "resolve"
        },
        {
            url: "https://expired.badssl.com/",
            expect: "reject"
        },
        {
            url: "https://wrong.host.badssl.com/",
            expect: "reject"
        },
        {
            url: "https://self-signed.badssl.com/",
            expect: "reject"
        },
        {
            url: "https://untrusted-root.badssl.com/",
            expect: "reject"
        },
        // Prohibited ciphers
        {
            url: "https://rc4-md5.badssl.com/",
            expect: "reject"
        },
        {
            url: "https://rc4.badssl.com/",
            expect: "reject"
        },
        {
            url: "https://3des.badssl.com/",
            expect: "reject"
        },
        {
            url: "https://null.badssl.com/",
            expect: "reject"
        },
        // HTTP
        {
            // This redirects to http
            url: "https://http.badssl.com/",
            expect: "reject"
        },
        {
            url: "http://http.badssl.com/",
            expect: "reject"
        },
        {
            // Unopened port (hopefully)
            url: "https://127.0.0.1:51749",
            expect: "reject"
        }
    ]);

    var filePrefix = path.join(os.tmpdir(), "gpii-test-download" + Math.random().toString(36) + "-");

    var files = [];
    // Remove all temporary files.
    gpii.tests.iod.teardowns.push(function () {
        fluid.each(files, function (file) {
            try {
                fs.unlinkSync(file);
            } catch (e) {
                // ignore.
            }
        });
    });


    var tests = gpii.tests.iod.installer.downloadTests;
    jqUnit.expect(tests.length * 3);

    var testIndex = -1;
    var nextTest = function () {
        if (++testIndex >= tests.length) {
            jqUnit.start();
            return;
        }

        var test = tests[testIndex];
        var suffix = " - test " + testIndex + "(" + test.url + ")";

        var outFile = filePrefix + testIndex;
        files.push(outFile);

        var p = gpii.iod.httpsDownload(test.url, outFile);

        jqUnit.assertTrue("httpsDownload must return a promise" + suffix, fluid.isPromise(p));

        p.then(function () {
            jqUnit.assertNotEquals("httpsDownload must only succeed if expected" + suffix, test.expect, "reject");

            if (test.expect === "resolve") {
                jqUnit.assert("resolved");
                nextTest();
            } else if (test.expect !== "reject") {
                var input = fs.createReadStream(outFile);
                var hash = crypto.createHash("sha1");
                input.on("readable", function () {
                    var buffer = input.read();
                    if (buffer) {
                        hash.update(buffer);
                    } else {
                        var digest = hash.digest("hex");
                        jqUnit.assertEquals("Hash of download must be correct", test.expect, digest);
                        nextTest();
                    }
                });

                input.on("error", function (err) {
                    fluid.log(err);
                    jqUnit.fail(err);
                });
            }
        }, function (err) {
            jqUnit.assertEquals("httpsDownload must only reject if expected" + suffix, test.expect, "reject");
            jqUnit.assert("Balancing the expected assert count");
            if (test.expects !== "reject") {
                fluid.log(err);
            }
            nextTest();
        });

    };

    nextTest();
});
