/* eslint-env browser */
/* eslint strict: ["error", "function"] */

/* global jqUnit, fluid */

(function () {
    "use strict";

    var gpii = fluid.registerNamespace("gpii");

    jqUnit.test("Test gpii.userErrors.isConnectionError()", function () {
        var testCases = [{
            code: "ECONNREFUSED",
            expected: true
        }, {
            code: "ETIMEDOUT",
            expected: true
        }, {
            code: "ECONNRESET",
            expected: true
        }, {
            code: "NONERROR",
            expected: false
        }];

        fluid.each(testCases, function (oneTestCase) {
            jqUnit.assertEquals("The return on the connection error code is expected", oneTestCase.expected, gpii.userErrors.isConnectionError(oneTestCase.code));
        });
    });

})();
