(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.require("gpiiFramework", require);

    fluid.defaults("gpii.app", {
        gradeNames: ["fluid.eventedComponent", "autoInit"],
        preInitFunction: "gpii.app.preInit",
        finalInitFunction: "gpii.app.finalInit",
        handlers: {},
        events: {
            contributeHandlers: null
        },
        listeners: {
            contributeHandlers: "{gpii.server}.amalgamateHandlers"
        }
    });

    gpii.app.preInit = function (that) {
        if (!that.options.typeName) {
            return;
        }
        that.context = fluid.typeTag(that.options.typeName);
    };

    gpii.app.finalInit = function (that) {
        that.events.contributeHandlers.fire(that.options.handlers);
    };

})();