"use strict";

var fluid = fluid || require("infusion"),
    gpii = fluid.registerNamespace("gpii"),
    fs = require("fs"),
    JSON5 = require("json5");

fluid.defaults("gpii.lifecycleManager.qssSettingsLoader", {
    gradeNames: ["fluid.component"],
    // The path to the file that has all QSS supported settings
    qssSettingsFilePath: "%gpii-universal/testData/solutions/qssSettings.json5",
    invokers: {
        get: {
            funcName: "gpii.lifecycleManager.qssSettingsLoader.get",
            args: ["{that}.options.qssSettingsFilePath"]
        }
    }
});

/**
 * Read QSS settings from the file system
 *
 * @param {String} qssSettingsFilePath - The path to the QSS settings file
 * @return {Object} QSS supported settings.
 */
gpii.lifecycleManager.qssSettingsLoader.get = function (qssSettingsFilePath) {
    return JSON5.parse(fs.readFileSync(fluid.module.resolvePath(qssSettingsFilePath), "utf-8"));
};
