var fluid = require("infusion");

var log = fluid.registerNamespace("gpii.matchMaker.log");

log.inAutoFile = function (preferences, suffix) {
	var fs = require('fs');
	var path = require('path');
	var curDate = new Date();
		fluid.log("=============== MM ===================");
		fluid.log(path.resolve(__dirname, "/logs/" + curDate.getTime() + "_" + suffix + ".json"));
		fluid.log("=============== MM ===================");
	fs.mkdir(path.resolve(__dirname, "/logs"))
	fs.writeFile(path.resolve(__dirname, "/logs/" + curDate.getTime() + "_" + suffix + ".json"), JSON.stringify(preferences));

};
