/*global jqUnit */

var fluid = fluid || require("infusion"),
    gpii = fluid.registerNamespace("gpii");

(function () {
    "use strict";
    fluid.registerNamespace("gpii.tests.matchMakerUtilities.filterPreferences");

    gpii.tests.matchMakerUtilities.filterPreferences.testDefs = [{
        name: "Basic common terms filtering",
        preferences: {
            "contexts": {
                "gpii-default": {
                    "name": "Default preferences",
                    "preferences": {
                        "http://registry.gpii.net/common/fontSize": 24,
                        "http://registry.gpii.net/common/magnification": 2
                    }
                }
            }
        },
        inferred: {
            "gpii-default": {
                "applications": {
                    "com.microsoft.windows.magnifier": {
                        "active": true,
                        "settings": {
                            "http://registry.gpii.net/common/magnification": 2
                        }
                    }
                }
            }
        },
        expected: {
            "contexts": {
                "gpii-default": {
                    "name": "Default preferences",
                    "preferences": {
                        "http://registry.gpii.net/common/magnification": 2
                    }
                }
            }
        }
    },{
        name: "Nested common terms filtering",
        preferences: {
            "contexts": {
                "gpii-default": {
                    "name": "Default preferences",
                    "preferences": {
                        "http://registry.gpii.net/applications/com.microsoft.windows.magnifier": {
                            "http://registry.gpii.net/common/fontSize": 24,
                            "http://registry.gpii.net/common/magnification": 2
                        }
                    }
                }
            }
        },
        inferred: {
            "gpii-default": {
                "applications": {
                    "com.microsoft.windows.magnifier": {
                        "active": true,
                        "settings": {
                            "http://registry.gpii.net/common/magnification": 2
                        }
                    }
                }
            }
        },
        expected: {
            "contexts": {
                "gpii-default": {
                    "name": "Default preferences",
                    "preferences": {
                        "http://registry.gpii.net/applications/com.microsoft.windows.magnifier": {
                            "http://registry.gpii.net/common/magnification": 2
                        }
                    }
                }
            }
        }
    }, {
        name: "Filtering with application specific settings",
        preferences: {
            "contexts": {
                "gpii-default": {
                    "preferences": {
                        "http://registry.gpii.net/applications/com.microsoft.windows.magnifier": {
                            "http://registry.gpii.net/common/fontSize": 24,
                            "http://registry.gpii.net/common/magnification": 2,
                            "Invert": 0
                        }
                    }
                }
            }
        },
        inferred: {
            "gpii-default": {
                "applications": {
                    "com.microsoft.windows.magnifier": {
                        "active": true,
                        "settings": {
                            "http://registry.gpii.net/common/magnification": 2,
                            "http://registry.gpii.net/applications/com.microsoft.windows.magnifier": {
                                "Invert": 0
                            }
                        }
                    }
                }
            }
        },
        expected: {
            "contexts": {
                "gpii-default": {
                    "preferences": {
                        "http://registry.gpii.net/applications/com.microsoft.windows.magnifier": {
                            "http://registry.gpii.net/common/magnification": 2,
                            "Invert": 0
                        }
                    }
                }
            }
        }
    }, {
        name: "Filtering with different contexts",
        preferences: {
            "contexts": {
                "gpii-default": {
                    "preferences": {
                        "http://registry.gpii.net/common/fontSize": 24,
                        "http://registry.gpii.net/common/magnification": 2
                    }
                },
                "other-context": {
                    "preferences": {
                        "http://registry.gpii.net/common/magnification": 2,
                        "http://registry.gpii.net/common/magnifierPosition": "Lens"
                    }
                }
            }
        },
        inferred: {
            "gpii-default": {
                "applications": {
                    "com.microsoft.windows.magnifier": {
                        "active": true,
                        "settings": {
                            "http://registry.gpii.net/common/magnification": 2
                        }
                    }
                }
            },
            "other-context": {
                "applications": {
                    "com.microsoft.windows.magnifier": {
                        "active": true,
                        "settings": {
                            "http://registry.gpii.net/common/magnification": 2,
                            "http://registry.gpii.net/common/magnifierPosition": "Lens"
                        }
                    }
                }
            }
        },
        expected: {
            "contexts": {
                "gpii-default": {
                    "preferences": {
                        "http://registry.gpii.net/common/magnification": 2
                    }
                },
                "other-context": {
                    "preferences": {
                        "http://registry.gpii.net/common/magnification": 2,
                        "http://registry.gpii.net/common/magnifierPosition": "Lens"
                    }
                }
            }
        }
    }, {
        name: "Filter non-matching application block",
        preferences: {
            "contexts": {
                "gpii-default": {
                    "name": "Default preferences",
                    "preferences": {
                        "http://registry.gpii.net/applications/com.microsoft.windows.magnifier": {
                            "http://registry.gpii.net/common/fontSize": 24,
                            "http://registry.gpii.net/common/magnification": 2
                        },
                        "http://registry.gpii.net/common/showCrosshairs": true
                    }
                }
            }
        },
        inferred: {
            "gpii-default": {
                "applications": {
                    "org.gnome.desktop.a11y.magnifier": {
                        "active": true,
                        "settings": {
                            "http://registry.gpii.net/common/magnification": 2,
                            "http://registry.gpii.net/common/showCrosshairs": true
                        }
                    }
                }
            }
        },
        expected: {
            "contexts": {
                "gpii-default": {
                    "name": "Default preferences",
                    "preferences": {
                        "http://registry.gpii.net/common/showCrosshairs": true
                    }
                }
            }
        }
    }];

    // non-matching application block
    // matching application block with no preferences in it

    gpii.tests.matchMakerUtilities.filterPreferences.runTests = function () {
        jqUnit.module("MatchMakerUtilities - filtering preferences from inferred configuration");
        fluid.each(gpii.tests.matchMakerUtilities.filterPreferences.testDefs, function (test) {
            jqUnit.test(test.name, function () {
                var result = gpii.matchMakerFramework.utils.filterPreferencesFromInferredConfig(test.preferences, test.inferred);
                jqUnit.assertDeepEq("Expecting the filtered preference set", test.expected, result);
            });
        });
    };
})();
