(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii"),
        kettle = fluid.require("kettle", require);

    kettle.requests.request.handler.ontologyConvertPost = function (that, requestProxy, to, preferences, ontologySource) {
        if (to !== "ISO24751-2") {
            requestProxy.events.onError.fire({
                isError: true,
                message: "Ontology server currently support only transformations to ISO24751-2 ontology."
            });
            return;
        }
        var source = gpii.ontologyServer.parsePreferences(preferences, to);
        if (source.from === source.to) {
            requestProxy.events.onSuccess.fire(preferences);
            return;
        }
        that.when(ontologySource.get({
            name: source.transformation
        }), function (transformations) {
            var transformed;
            transformations = gpii.ontologyServer.expandTransformations(transformations);
            transformed = fluid.model.transformWithRules(preferences, transformations);
            return transformed;
        });
    };

})();