"use strict";

var fluid = require("infusion"),
    gpii = fluid.registerNamespace("gpii");

fluid.defaults("gpii.preferencesServer.add.post.handler", {
    gradeNames: ["kettle.request.http"],
    invokers: {
        handleRequest: {
            funcName: "gpii.preferencesServer.add.post.handleRequest",
            args: [ "{preferencesServer}", "{request}", "{that}.events.onRawPreferences", "{preferencesServer}.authGrantFinder"]
        },
        postHandler: {
            func: "{preferencesServer}.setPrefsReturnHandler",
            args: [ "{arguments}.0", "{request}"]
        }
    },
    events: {
        onRawPreferences: null
    },
    listeners: {
        onRawPreferences: "{that}.postHandler"
    }
});

gpii.preferencesServer.add.post.handleRequest = function (preferencesServer, request, event, authGrantFinder) {
    var authorizationPromise = gpii.oauth2.getAuthorization(request.req, authGrantFinder);
    authorizationPromise.then(function (authorization) {
        if (!authorization || !authorization.allowAddPrefs) {
            request.events.onError.fire(gpii.oauth2.errors.unauthorized);
            return;
        }

        preferencesServer.setPrefsRequestHandler(request, event);
    }, function () {
        request.events.onError.fire(gpii.oauth2.errors.unauthorized);
    });
};
