/* global require */
(function () {
    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.registerNamespace("gpii.preferencesServer.get");

    fluid.defaults("kettle.requests.request.handler.preferencesGet", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        invokers: {
            handle: {
                funcName: "gpii.preferencesServer.get.getRawPreferences",
                args: [ "{preferencesServer}", "{request}", "{that}.events.onRawPreferences"],
                dynamic: true
            },
            buildReturnPayload: {
                funcName: "gpii.preferencesServer.get.buildReturnPayload",
                args: [ "{arguments}.0", "{preferencesServer}.ontologyHandler", "{request}", "{requestProxy}" ]
            }
        },
        events: {
            onRawPreferences: null
        },
        listeners: {
            onRawPreferences: "{that}.buildReturnPayload"
        }
    });

    gpii.preferencesServer.get.getRawPreferences = function (preferencesServer, request, event) {
        preferencesServer.getRawPreferences(event, request.req.params.userToken);
    };

    gpii.preferencesServer.get.buildReturnPayload = function (rawPrefs, ontologyHandler, request, requestProxy) {
        fluid.log("Raw preferences fetched by preferencesServer: "+rawPrefs);
        // grab the desired view (ontology) value from parameter (default to "flat") and GET
        var toView = gpii.preferencesServer.getRequestView(request);
        var prefs = ontologyHandler.rawPrefsToOntology(rawPrefs, toView);
        requestProxy.events.onSuccess.fire(prefs);
    };
})();