"use strict";

var fluid = require("infusion"),
    gpii = fluid.registerNamespace("gpii");

fluid.defaults("gpii.preferencesServer.put.handler", {
    gradeNames: ["kettle.request.http"],
    invokers: {
        handleRequest: {
            funcName: "gpii.preferencesServer.post.handler.updatePreferences",
            args: ["{preferencesServer}", "{that}", "{that}.req.params.gpiiKey", "{that}.req.body", "{that}.req.query.view", "{that}.req.query.merge"]
        }
    }
});

gpii.preferencesServer.post.handler.updatePreferences = function (preferencesServer, request, gpiiKey, preferences, view, merge) {
    merge = (merge === "true");   // convert string to boolean
    var prefsPromise = preferencesServer.updatePreferences(gpiiKey, preferences, view, merge);
    prefsPromise.then(request.events.onSuccess.fire, function (error) {
        request.events.onError.fire({
            isError: true,
            message: error.message,
            statusCode: 404
        });
    });
};
