(function () {

    "use strict";

    var fluid = require("infusion"),
        path = require("path"),
        gpii = fluid.registerNamespace("gpii");

    fluid.require("kettle", require);
    fluid.require("./userGet.js", require);
    fluid.require("./userPost.js", require);

    fluid.defaults("gpii.preferencesServer", {
        gradeNames: ["kettle.app", "autoInit"],
        handlers: {
            userGet: {
                route: "/user/:token",
                type: "get"
            },
            userPost: {
                route: "/user/:token",
                type: "post"
            }
        },
        root: path.join(__dirname, ".."),
        userSourceUrl: "",
        components: {
            rawUserSource: {
                type: "kettle.dataSource",
                options: {
                    url: "{gpii.preferencesServer}.options.userSourceUrl",
                    writable: true,
                    termMap: {
                        token: "%token"
                    }
                }
            },
            userSource: {
                type: "kettle.callbackWrappingDataSource"
            }
        }
    });

})();