(function () {

    "use strict";

    var fluid = require("infusion"),
        path = require("path");

    fluid.require("./userGet.js", require);
    fluid.require("./userPost.js", require);

    fluid.defaults("gpii.preferencesServer", {
        gradeNames: ["kettle.app", "autoInit"],
        handlers: {
            userGet: {
                route: "/user/:token",
                type: "get"
            },
            userPost: {
                route: "/user/:token?",
                type: "post"
            }
        },
        root: path.join(__dirname, ".."),
        userSourceUrl: "",
        components: {
            userSource: {
                type: "kettle.dataSource.CouchDB",
                options: {
                    gradeNames: ["kettle.dataSource.simpleCallbackWrapper"],
                    url: "{gpii.preferencesServer}.options.userSourceUrl",
                    writable: true,
                    termMap: {
                        token: "%token"
                    }
                }
            }
        }
    });

    fluid.defaults("gpii.preferencesServer.dev", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        components: {
            userSource: {
                type: "kettle.dataSource.URL"
            }
        },
        distributeOptions: {
            source: "{that}.options.urlExpanderGradeNames",
            target: "{that urlExpander}.options.gradeNames"
        },
        urlExpanderGradeNames: ["kettle.urlExpander.development"]
    });

})();
