(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii"),
        kettle = fluid.require("kettle", require);

    fluid.defaults("kettle.requests.request.handler.userGet", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        invokers: {
            handle: {
                funcName: "gpii.handleUserGet",
                args: ["{requestProxy}", "{request}.req.params.token", "{preferencesServer}.userSource"],
                dynamic: true
            }
        }
    });

    gpii.handleUserGet = function (requestProxy, token, userSource) {
        userSource.get({
            token: token
        }, function (preferences) {
            requestProxy.events.onSuccess.fire({
                token: token,
                preferences: preferences
            });
        });
    };

})();
