(function () {

    "use strict";

    var fluid = require("infusion"),
        uuid = require("node-uuid"),
        gpii = fluid.registerNamespace("gpii");

    fluid.defaults("kettle.requests.request.handler.userPost", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        invokers: {
            handle: {
                funcName: "gpii.handleUserPost",
                args: ["{requestProxy}", "{request}.req", "{preferencesServer}.userSource"],
                dynamic: true
            }
        }
    });

    gpii.handleUserPost = function (requestProxy, req, userSource) {
        var directModel = {
            userToken: req.params.userToken || uuid.v4()
        };
        var preferences = req.body; // TODO: for some reason this is already in JSON form

        userSource.set(directModel, preferences, function () {
            gpii.preferencesServer.handlePreferences(requestProxy, directModel.userToken, preferences);
        });
    };

})();
