(function () {

    "use strict";

    var fluid = require("infusion"),
        uuid = require("node-uuid"),
        kettle = fluid.require("kettle", require);

    kettle.requests.request.handler.userPost = function (requestProxy, req, userSource) {
        userSource.set({
            token: req.params.token || uuid.v4()
        }, req.body, requestProxy.events.onSuccess.fire);
    };

})();