"use strict";

var fluid = require("infusion"),
    jqUnit = fluid.require("node-jqunit", require, "jqUnit"),
    uuid = require("node-uuid"),
    kettle = require("kettle"),
    gpii = fluid.registerNamespace("gpii"),
    fs = require("fs");

require("preferencesServer");

kettle.loadTestingSupport();

fluid.registerNamespace("gpii.tests.preferencesServer");

gpii.tests.preferencesServer.prefsDir = fluid.module.resolvePath("%universal/gpii/testData/preferences/");

gpii.tests.preferencesServer.standardConfig = {
    configName: "gpii.tests.preferencesServer.config.standard",
    configPath: "%preferencesServer/test/configs"
};

gpii.tests.preferencesServer.filesToDelete = [];

gpii.tests.preferencesServer.cleanUpTmpFiles = function () {
    var file;
    while ((file = gpii.tests.preferencesServer.filesToDelete.pop()) !== undefined) {
        fs.unlinkSync(file);
    }
};

fluid.defaults("gpii.tests.preferencesServer.testServerMock", {
    gradeNames: ["fluid.component"],
    invokers: {
        set: {
            funcName: "gpii.tests.preferencesServer.testServerMock.set",
            args: [ "{arguments}.0", "{arguments}.1", "{arguments}.2", "{that}.backingRawPreferencesDataSource"]
        },
        get: {
            funcName: "gpii.tests.preferencesServer.testServerMock.get",
            args: [ "{arguments}.0", "{arguments}.1", "{arguments}.2", "{that}.backingRawPreferencesDataSource"]
        }
    },
    components: {
        backingRawPreferencesDataSource: { // not "rawPreferencesDataSource" to avoid runaway like https://issues.fluidproject.org/browse/FLUID-5305
            type: "kettle.dataSource.file",
            options: {
                gradeNames: "kettle.dataSource.file.moduleTerms",
                path: "%preferencesServer/test/data/%userToken.json",
                termMap: {
                    userToken: "%userToken"
                },
                writable: true
            }
        }
    }
});

gpii.tests.preferencesServer.testServerMock.set = function (model, body, callback, rawPreferencesDataSource) {
    // assert that this function is called with a body:
    jqUnit.assertTrue("Set function of rawPreferencesServer mock is called", true);
    var userToken = model.userToken;
    if (userToken === "" || userToken === undefined) {
        userToken = uuid.v4();
    }

    gpii.tests.preferencesServer.filesToDelete.push(__dirname + "/data/" + userToken + ".json");

    var promise = fluid.promise();
    rawPreferencesDataSource.set({userToken: userToken}, body, callback).then(function () {
        promise.resolve({ userToken: userToken, preferences: body});
    });
    return promise;
};

gpii.tests.preferencesServer.testServerMock.get = function (model, body, callback, rawPreferencesDataSource) {
    var promise = fluid.promise();
    rawPreferencesDataSource.get(model, body, callback).then(function (data) {
        promise.resolve({ userToken: model.userToken, preferences: data});
    }, function (err) {
        promise.reject({ "isError": true, "statusCode": 404, message: err.message });
    });
    return promise;
};

/////////////////////// TESTING preferencesServer GET functionality ////////////////////////////
fluid.registerNamespace("gpii.tests.preferencesServer.get");

gpii.tests.preferencesServer.get.testRequestResponse = function (expected) {
    return function (data) {
        var retrievedData = JSON.parse(data);
        fluid.log("preferencesServer.testRequestResponse received " + JSON.stringify(retrievedData, null, 4));
        jqUnit.assertDeepEq("Response is correct", expected, retrievedData);
    };
};

gpii.tests.preferencesServer.get.buildTestDef = function (fixture) {
    return {
        name: fixture.name,
        expect: 1,
        config: gpii.tests.preferencesServer.standardConfig,
        components: {
            getRequest: {
                type: "kettle.test.request.http",
                options: {
                    path: "/preferences/" + fixture.url,
                    method: "GET",
                    port: 8081,
                    termMap: {
                        userToken: fixture.userToken
                    }
                }
            }
        },
        sequence: [{
            func: "{getRequest}.send"
        }, {
            event: "{getRequest}.events.onComplete",
            listenerMaker: "gpii.tests.preferencesServer.get.testRequestResponse",
            makerArgs: [ fixture.expected ]
        }]
    };
};

