fluid.registerNamespace("gpii.tests.preferencesServer.post");

gpii.tests.preferencesServer.post.testRequestResponse = function (prefs) {
    return function (data) {
        var retrievedData = JSON.parse(data);
        // expect preferences without ontology info - any generated user token is fine
        var expected = {
            preferences: prefs,
            userToken: retrievedData.userToken
        };

        jqUnit.assertDeepEq("Response from preferences server is as expected", expected, retrievedData);
    };
};

gpii.tests.preferencesServer.post.buildTestDef = function (fixture) {
    return {
        name: fixture.name,
        expect: 2,
        config: gpii.tests.preferencesServer.standardConfig,
        listeners: {
            onDestroy: gpii.tests.preferencesServer.cleanUpTmpFiles
        },
        components: {
            postRequest: {
                type: "kettle.test.request.http",
                options: {
                    path: "/preferences",
                    method: "POST",
                    port: 8081
                }
            }
        },
        sequence: [{
            func: "{postRequest}.send",
            args: fixture.postBody
        }, {
            event: "{postRequest}.events.onComplete",
            listenerMaker: "gpii.tests.preferencesServer.post.testRequestResponse",
            makerArgs: [ fixture.postBody ]
        }]
    };
};

