// To test the complete work flow of /add-preferences POST, it requires the presence of the flow manager with
// the OAuth security server. That said, in a cloud based platform. Therefore, this test is written at
// /tests/platform/cloud/AcceptanceTests_oauth2_addPrefs.js


// =============== Rejected promise from rawPreferences server =========
fluid.defaults("gpii.tests.preferencesServer.rejectedPromise", {
    gradeNames: ["fluid.component"],
    invokers: {
        set: {
            funcName: "gpii.tests.preferencesServer.rejectedPromise.set"
        },
        get: {
            funcName: "gpii.tests.preferencesServer.rejectedPromise.get"
        }
    }
});

gpii.tests.preferencesServer.rejectedPromise.set = function () {
    var promise = fluid.promise();
    promise.reject({
        isError: true,
        message: "this is a failure"
    });
    return promise;
};

gpii.tests.preferencesServer.rejectedPromise.get = function () {
    var promise = fluid.promise();
    promise.reject({
        isError: true,
        message: "this is a failure"
    });
    return promise;
};

gpii.tests.preferencesServer.testRejectedPromise = function (request) {
    return function (data) {
        data = JSON.parse(data);
        jqUnit.assertTrue("Received error as expected", data.isError);
        jqUnit.assertEquals("Received message as expected", "this is a failure", data.message);
        jqUnit.assertEquals("Received error code 500", 500, request.nativeResponse.statusCode);
    };
};

