// Run tests for /health endpoint:
// 1. With a proper database connection: readiness === true
gpii.test.bootstrapServer(gpii.tests.preferencesServer.health.testDefs);

// 2. Without a database connection: readiness === true
kettle.test.bootstrapServer(gpii.tests.preferencesServer.health.testDefs);

// Run tests for /ready endpoint:
// 1. With a proper database connection: liveness === true
gpii.test.bootstrapServer(gpii.tests.preferencesServer.ready.testDefsWithDbConnection);

// 2. Without a database connection: liveness === false
kettle.test.bootstrapServer(gpii.tests.preferencesServer.ready.testDefsWithoutDbConnection);
