(function () {
    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.defaults("kettle.requests.request.handler.rawPreferencesGet", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        invokers: {
            handle: {
                funcName: "gpii.handleRawPreferencesGet",
                args: ["{requestProxy}", "{request}.req.params.userToken", "{rawPreferencesServer}.rawPreferencesSource"],
                dynamic: true
            }
        }
    });

    gpii.handleRawPreferencesGet = function (requestProxy, userToken, rawPreferencesSource) {
        rawPreferencesSource.get({
            userToken: userToken
        }, function (preferences) {
            gpii.rawPreferencesServer.handlePreferences(requestProxy, userToken, preferences);
        });
    };

})();
