(function () {
    "use strict";

    var fluid = require("infusion"),
        uuid = require("node-uuid"),
        gpii = fluid.registerNamespace("gpii");

    fluid.defaults("kettle.requests.request.handler.rawPreferencesPost", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        invokers: {
            handle: {
                funcName: "gpii.handleRawPreferencesPost",
                args: ["{requestProxy}", "{request}.req", "{rawPreferencesServer}.rawPreferencesSource"],
                dynamic: true
            }
        }
    });

    gpii.handleRawPreferencesPost = function (requestProxy, req, rawPreferencesSource) {
        var userToken = uuid.v4();
        rawPreferencesSource.set({
            userToken: userToken
        }, req.body, function () {
            gpii.rawPreferencesServer.handlePreferences(requestProxy, userToken, req.body);
        });
    };
})();
