(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.defaults("kettle.requests.request.handler.rawPreferencesPut", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        invokers: {
            handle: {
                funcName: "gpii.handleRawPreferencesPut",
                args: ["{requestProxy}", "{request}.req", "{rawPreferencesServer}.rawPreferencesSource"],
                dynamic: true
            }
        }
    });

    gpii.handleRawPreferencesPut = function (requestProxy, req, rawPreferencesSource) {
        var userToken = req.params.userToken;
        rawPreferencesSource.set({
            userToken: userToken
        }, req.body, function () {
            gpii.rawPreferencesServer.handlePreferences(requestProxy, userToken, req.body);
        });
    };

})();
