(function () {

    "use strict";

    var fluid = require("infusion"),
        path = require("path");

    fluid.require("kettle", require);
    fluid.require("./rawPreferencesGet.js", require);
    fluid.require("./rawPreferencesPost.js", require);
    fluid.require("./rawPreferencesPut.js", require);

    fluid.defaults("gpii.rawPreferencesServer", {
        gradeNames: ["kettle.app", "autoInit"],
        handlers: {
            rawPreferencesGet: {
                route: "/rawPreferences/:userToken",
                type: "get"
            },
            rawPreferencesPost: {
                route: "/rawPreferences",
                type: "post"
            },
            rawPreferencesPut: {
                route: "/rawPreferences/:userToken",
                type: "put"
            }
        },
        root: path.join(__dirname, ".."),
        rawPreferencesSourceUrl: "",
        components: {
            rawPreferencesSource: {
                type: "kettle.dataSource.CouchDB",
                options: {
                    url: "{gpii.rawPreferencesServer}.options.rawPreferencesSourceUrl",
                    writable: true,
                    termMap: {
                        userToken: "%userToken"
                    }
                }
            }
        }
    });

    fluid.defaults("gpii.rawPreferencesServer.dev", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        components: {
            rawPreferencesSource: {
                type: "kettle.dataSource.URL"
            }
        },
        distributeOptions: {
            source: "{that}.options.urlExpanderGradeNames",
            target: "{that urlExpander}.options.gradeNames"
        },
        urlExpanderGradeNames: ["kettle.urlExpander.development"]
    });

})();
