"use strict";

var fluid = require("infusion"),
    kettle = require("kettle"),
    jqUnit = fluid.require("node-jqunit", require, "jqUnit"),
    gpii = fluid.registerNamespace("gpii"),
    fs = require("fs");

require("rawPreferencesServer");

kettle.loadTestingSupport();

fluid.registerNamespace("gpii.tests.rawPreferencesServer");

gpii.tests.rawPreferencesServer.standardConfig = {
    configName: "gpii.tests.rawPreferencesServer.config",
    configPath: "%rawPreferencesServer/test/configs"
};

gpii.tests.rawPreferencesServer.testDefs = [];

/* ---------------------- TESTING GET FUNCTIONALITY --------------------------- */
fluid.registerNamespace("gpii.tests.rawPreferencesServer.get");

gpii.tests.rawPreferencesServer.get.successfulRequestAssert = function (userToken) {
    return function (data) {
        data = JSON.parse(data);

        // read expected preferences directly from preferences file
        var rawPrefs = fs.readFileSync(__dirname + "/data/" + userToken + ".json");
        var expected = {
            userToken: userToken,
            preferences: JSON.parse(rawPrefs)
        };

        jqUnit.assertDeepEq("Response is correct", expected, data);
    };
};

gpii.tests.rawPreferencesServer.get.failingRequestAssert = function (data, request) {
    data = JSON.parse(data);
    // If a user token doesn't exist, expect the preferences part to be undefined
    jqUnit.assertTrue("Checking that we're getting an error for nonexisting user token", data.isError);
    jqUnit.assertTrue("Checking that we're getting a 404 error code for nonexisting user token", request.nativeResponse.statusCode === 404);
};

gpii.tests.rawPreferencesServer.get.buildTestDef = function (fixture) {
    var sequence = [{
        func: "{getRequest}.send"
    }];
    sequence.push(fixture.onCompleteSequence);

    return {
        name: fixture.name,
        expect: fixture.expect,
        config: gpii.tests.rawPreferencesServer.standardConfig,
        components: {
            getRequest: {
                type: "kettle.test.request.http",
                options: {
                    path: "/rawPreferences/%userToken",
                    port: 8078,
                    termMap: {
                        userToken: fixture.userToken
                    }
                }
            }
        },
        sequence: sequence
    };
};

gpii.tests.rawPreferencesServer.get.fixtures = [
    {
        name: "Raw Preferences Server - basic GET test",
        expect: 1,
        userToken: "np_flatonly",
        onCompleteSequence: {
            event: "{getRequest}.events.onComplete",
            listenerMaker: "gpii.tests.rawPreferencesServer.get.successfulRequestAssert",
            makerArgs: [ "np_flatonly" ]
        }
    }, {
        name: "Raw Preferences Server - empty NP set",
        expect: 1,
        userToken: "np_empty",
        onCompleteSequence: {
            event: "{getRequest}.events.onComplete",
            listenerMaker: "gpii.tests.rawPreferencesServer.get.successfulRequestAssert",
            makerArgs: [ "np_empty" ]
        }
    }, {
        name: "Raw Preferences Server - failing GET test (non-existing user token)",
        expect: 2,
        userToken: "bogus_np",
        onCompleteSequence: {
            event: "{getRequest}.events.onComplete",
            listener: "gpii.tests.rawPreferencesServer.get.failingRequestAssert"
        }
    }
];

fluid.each(gpii.tests.rawPreferencesServer.get.fixtures, function (fixture) {
    gpii.tests.rawPreferencesServer.testDefs.push(gpii.tests.rawPreferencesServer.get.buildTestDef(fixture));
});

/* ---------------------- TESTING POST FUNCTIONALITY --------------------------- */
fluid.registerNamespace("gpii.tests.rawPreferencesServer.post");

gpii.tests.rawPreferencesServer.post.assertNewNPResponse = function (data) {
    data = JSON.parse(data);
    jqUnit.assertTrue("Structure of returned payload should contain user token and preferences",
        data.preferences !== undefined && data.userToken !== undefined);
    // saved prefs set should contain the preferences
    var fsPrefs = fs.readFileSync(__dirname + "/data/" + data.userToken + ".json");
    jqUnit.assertDeepEq("Expecting preferences to be those posted response",
        JSON.parse(fsPrefs), data.preferences);

    // clean up the created NP set
    fs.unlinkSync(__dirname + "/data/" + data.userToken + ".json");
};

gpii.tests.rawPreferencesServer.post.buildTestDef = function (fixture) {
    return {
        name: fixture.name,
        expect: 2,
        config: gpii.tests.rawPreferencesServer.standardConfig,
        components: {
            postRequest: {
                type: "kettle.test.request.http",
                options: {
                    path: "/rawPreferences",
                    method: "POST",
                    port: 8078
                }
            }
        },
        sequence: [{
            func: "{postRequest}.send",
            args: fixture.postBody
        }, {
            event: "{postRequest}.events.onComplete",
            listener: "gpii.tests.rawPreferencesServer.post.assertNewNPResponse"
        }]
    };
};

gpii.tests.rawPreferencesServer.post.fixtures = [
    {
        name: "POST: With no user token specified",
        postBody: {
            "flat": {
                "contexts": {
                    "gpii-default": {
                        "name": "Default preferences",
                        "preferences": {
                            "http://registry.gpii.net/common/onScreenKeyboardEnabled": [{ "value": true }],
                            "http://registry.gpii.net/common/initDelay": [{ "value": 0.120 }],
                            "http://registry.gpii.net/common/cursorSpeed": [{ "value": 0.850 }],
                            "http://registry.gpii.net/common/cursorAcceleration": [{ "value": 0.800 }],
                            "http://registry.gpii.net/common/mouseEmulationEnabled": [{ "value": true }],
                            "http://registry.gpii.net/common/unknown": [{ "value": true }]
                        }
                    }
                },
                "metadata": []
            }
        }
    }
];

fluid.each(gpii.tests.rawPreferencesServer.post.fixtures, function (fixture) {
    gpii.tests.rawPreferencesServer.testDefs.push(gpii.tests.rawPreferencesServer.post.buildTestDef(fixture));
});


/* ---------------------- TESTING PUT FUNCTIONALITY --------------------------- */
fluid.registerNamespace("gpii.tests.rawPreferencesServer.put");

gpii.tests.rawPreferencesServer.put.assertResponse = function (userToken) {
    return function (data) {
        data = JSON.parse(data);
        var fsPrefs = fs.readFileSync(__dirname + "/data/" + userToken + ".json");

        jqUnit.assertDeepEq("Expecting return payload to contain prefs and user token",
             { userToken: userToken, preferences: JSON.parse(fsPrefs) }, data);
    };
};

gpii.tests.rawPreferencesServer.put.copyNPSet = function (from, to) {
    var fsPrefs = fs.readFileSync(__dirname + "/data/" + from + ".json");
    fs.writeFileSync(__dirname + "/data/" + to + ".json", fsPrefs);
};

gpii.tests.rawPreferencesServer.put.deleteNPSet = function (userToken) {
    fs.unlinkSync(__dirname + "/data/" + userToken + ".json");
};

gpii.tests.rawPreferencesServer.put.buildTestDef = function (fixture) {
    var testDef = {
        name: fixture.name,
        expect: 1,
        config: gpii.tests.rawPreferencesServer.standardConfig,
        components: {
            postRequest: {
                type: "kettle.test.request.http",
                options: {
                    path: "/rawPreferences/%userToken",
                    method: "PUT",
                    port: 8078,
                    termMap: {
                        userToken: fixture.userToken
                    }
                }
            }
        },
        sequence: [{
            func: "{postRequest}.send",
            args: fixture.putBody
        }, {
            event: "{postRequest}.events.onComplete",
            listenerMaker: "gpii.tests.rawPreferencesServer.put.assertResponse",
            makerArgs: [ fixture.userToken ]
        }, {
            func: "gpii.tests.rawPreferencesServer.put.deleteNPSet",
            args: [ fixture.userToken ]
        }]
    };

    if (fixture.preSequence) {
        testDef.sequence.unshift(fixture.preSequence);
    }
    return testDef;

};

gpii.tests.rawPreferencesServer.put.fixtures = [
    {
        name: "PUT: Using user token for new preferences set",
        userToken: "brand_new",
        putBody: {
            "flat": {
                "contexts": {
                    "gpii-default": {
                        "name": "Default preferences",
                        "preferences": {
                            "http://registry.gpii.net/common/onScreenKeyboardEnabled": [{ "value": true }],
                            "http://registry.gpii.net/common/mouseEmulationEnabled": [{ "value": true }],
                            "http://registry.gpii.net/common/unknown": [{ "value": true }]
                        }
                    }
                },
                "metadata": []
            }
        }
    }, {
        name: "PUT: Updating existing preferences set",
        userToken: "overwriter",
        putBody: {
            "flat": {
                "contexts": {
                    "gpii-default": {
                        "name": "Default preferences",
                        "preferences": {
                            "http://registry.gpii.net/common/onScreenKeyboardEnabled": [{ "value": true }],
                            "http://registry.gpii.net/common/initDelay": [{ "value": 0.120 }],
                            "http://registry.gpii.net/common/cursorSpeed": [{ "value": 0.850 }],
                            "http://registry.gpii.net/common/cursorAcceleration": [{ "value": 0.800 }],
                            "http://registry.gpii.net/common/mouseEmulationEnabled": [{ "value": true }],
                            "http://registry.gpii.net/common/unknown": [{ "value": true }]
                        }
                    }
                },
                "metadata": []
            }
        },
        preSequence: {
            func: "gpii.tests.rawPreferencesServer.put.copyNPSet",
            args: [ "np_flatonly", "overwriter" ]
        }
    }
];

fluid.each(gpii.tests.rawPreferencesServer.put.fixtures, function (fixture) {
    gpii.tests.rawPreferencesServer.testDefs.push(gpii.tests.rawPreferencesServer.put.buildTestDef(fixture));
});

module.exports = kettle.test.bootstrapServer(gpii.tests.rawPreferencesServer.testDefs);
