/*global require*/

(function () {

    "use strict";

    var fluid = require("infusion"),
        fs = require("fs"),
        gpii = fluid.registerNamespace("gpii"),
        iniparser = require("ini");

    fluid.registerNamespace("gpii.settingsHandlers.INISettingsHandler");

    var transformPayload = function (payload, modifier) {
        var returnObj = fluid.copy(payload);
        return fluid.transform(returnObj, function (solution) {
            return fluid.transform(solution, function(solutionEntry) {
                var options = solutionEntry.options;

                if (options && options.path) {
                    var currentSettings = iniparser.parse(fs.readFileSync(options.path, 'utf-8'), options);        
                    return modifier(solutionEntry, currentSettings);
                }
                return solutionEntry;
            });  
        });
    };

    gpii.settingsHandlers.INISettingsHandler.get = function (payload) {
        return transformPayload(payload, function (solutionEntry, currentSettings) {
            var newSettingsResponse = {};
            var userRequestedSettings = solutionEntry.settings;
            
            fluid.each(userRequestedSettings, function (settingVal, settingKey) {
                var value = fluid.get(currentSettings, settingKey, fluid.model.escapedGetConfig);
                newSettingsResponse[settingKey] = value;
            });

            return { options: solutionEntry.options, settings: newSettingsResponse };
        });
    };

    gpii.settingsHandlers.INISettingsHandler.set = function (payload) {
        var oldValue;

        return transformPayload(payload, function (solutionEntry, currentSettings) {
            var newSettingsResponse = {};
            var userRequestedSettings = solutionEntry.settings;
            var options = solutionEntry.options;

            // record differences between required and default settings
            // so that they default settings can be restored
            fluid.each(userRequestedSettings, function (settingVal, settingKey) {
                oldValue = fluid.get(currentSettings, settingKey, fluid.model.escapedGetConfig);
                fluid.set(currentSettings, settingKey, settingVal, fluid.model.escapedSetConfig);
                newSettingsResponse[settingKey] = {
                    "oldValue": oldValue,
                    "newValue": settingVal
                };
            });
            solutionEntry.settings = currentSettings;

            // Write the new settings to a file for the application to read
            var iniOutput = iniparser.stringify(currentSettings, null, options);
            fs.writeFileSync(options.path, iniOutput);
            fluid.log("Settings explicitly configured in GPII profile:", iniOutput);
            return { options: options, settings: newSettingsResponse };
        });
    };
}());