/*global require*/

(function () {

    "use strict";

    var fluid = require("infusion"),
        settingsHandlers = fluid.registerNamespace("gpii.settingsHandlers"),
        parser = fluid.registerNamespace(
            "gpii.settingsHandlers.INISettingsHandler.parser"),
        iniparser = require("ini");

    parser.parse = function (content, options) {
        return iniparser.parse(content, options);
    };

    parser.stringify = function (content, options) {
        return iniparser.stringify(content, null, options);
    };

    /* PUBLIC API FUNCTIONS */
    settingsHandlers.INISettingsHandler.get = settingsHandlers.makeFileGet(parser);
    settingsHandlers.INISettingsHandler.set = settingsHandlers.makeFileSet(parser);
}());
