/* marker grades for various contexts (generally the OS on which a solution runs) */
"use strict";
var fluid = require("infusion");

fluid.defaults("gpii.solutionsRegistry.context", {
    gradeNames: ["fluid.component"]
});

fluid.defaults("gpii.solutionsRegistry.context.windows", {
    gradeNames: ["gpii.solutionsRegistry.context"],
    contexts: {
        OS: {
            windows: {
                id: "win32",
                version: ">=5.0"
            }
        }
    }
});

fluid.defaults("gpii.solutionsRegistry.context.web", {
    gradeNames: ["gpii.solutionsRegistry.context"],
    contexts: [{
        OS: {
            web: {
                "id": "web"
            }
        }
    }]
});

fluid.defaults("gpii.solutionsRegistry.context.android", {
    gradeNames: ["gpii.solutionsRegistry.context"],
    contexts: {
        OS: {
            android: {
                id: "android",
                version: ">=0.1"
            }
        }
    }
});

