/* eslint-env node */
"use strict";
var fluid = require("infusion");

// The "live" solutions registry that assembles a hierarchy of solutions, settings handlers and settings as dynamic
// components.  The solutions holder is the outermost layer whose  `solutions` option that will be used to build one or
// more solution components.
fluid.defaults("gpii.solutionsRegistry.solutionsHolder", {
    gradeNames: ["fluid.component"],
    dynamicComponents: {
        solution: {
            sources: "{that}.options.solutions",
            type: "{source}"
        }
    }
});
