(function () {

    "use strict";

    var fluid = require("infusion"),
        kettle = fluid.require("kettle", require);

    kettle.requests.request.handler.solutionGet = function (id, requestProxy, solutionSource) {
        solutionSource.get({
            id: id
        }, requestProxy.events.onSuccess.fire);
    };

})();