(function () {

    "use strict";

    var fluid = require("infusion"),
        kettle = fluid.require("kettle", require);

    kettle.requests.request.handler.solutionPost = function (req, requestProxy, solutionSource) {
        solutionSource.set({
            id: req.params.id
        }, req.body, requestProxy.events.onSuccess.fire);
    };

})();