(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "solutionPost", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.solutionPost",
                    args: ["{request}.req", "{requestProxy}", "{solutionsRegistry}.solutionSource"]
                }
            }
        }
    });

    gpii.requests.request.handler.solutionPost = function (req, requestProxy, solutionSource) {
        solutionSource.set({
            id: req.params.id
        }, req.body, requestProxy.events.onSuccess.fire);
    };

})();