(function () {

    "use strict";

    var fluid = require("infusion"),
        path = require("path");

    fluid.require("./solutionGet.js", require);
    fluid.require("./solutionPost.js", require);
    fluid.require("./solutionQuery.js", require);

    fluid.defaults("gpii.solutionsRegistry", {
        gradeNames: ["kettle.app", "autoInit"],
        handlers: {
            solutionGet: {
                route: "/solution/:id",
                type: "get"
            },
            solutionPost: {
                route: "/solution/:id",
                type: "post"
            },
            solutionQuery: {
                route: "/solution?:query",
                type: "get"
            }
        },
        solutionsRegistryUrl: "",
        solutionQueryUrl: "",
        root: path.join(__dirname, ".."),
        components: {
            solutionSource: {
                type: "kettle.dataSource.CouchDB",
                options: {
                    url: "{gpii.solutionsRegistry}.options.solutionsRegistryUrl",
                    writable: true,
                    termMap: {
                        id: "%id"
                    }
                }
            },
            solutionQuerySource: {
                type: "kettle.dataSource.CouchDB",
                options: {
                    url: "{gpii.solutionsRegistry}.options.solutionQueryUrl",
                    termMap: {
                        os: "%os"
                    }
                }
            }
        }
    });

    fluid.defaults("gpii.solutionsRegistry.dev", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        components: {
            solutionSource: {
                type: "kettle.dataSource.URL"
            },
            solutionQuerySource: {
                type: "kettle.dataSource.URL"
            }
        },
        distributeOptions: {
            source: "{that}.options.urlExpanderGradeNames",
            target: "{that urlExpander}.options.gradeNames"
        },
        urlExpanderGradeNames: ["kettle.urlExpander.development"]
    });

})();
