(function () {

    "use strict";

    var fluid = require("infusion"),
        path = require("path"),
        gpii = fluid.registerNamespace("gpii");

    fluid.require("gpiiFramework", require);
    fluid.require("./solutionsGet.js", require);
    fluid.require("./solutionsPost.js", require);
    fluid.require("./solutionsQuery.js", require);
    
    fluid.defaults("gpii.solutionsRegistry", {
        gradeNames: ["gpii.app", "autoInit"],
        handlers: {
            solutionsGet: {
                route: "/solutions/:id",
                type: "get"
            },
            solutionsPost: {
                route: "/solutions/:id",
                type: "post"
            },
            solutionsQuery: {
                route: "/solutions?:query",
                type: "get"
            }
        },
        solutionsRegistryUrl: "",
        solutionsQueryUrl: "",
        components: {
            solutionsSource: {
                type: "gpii.dataSource",
                options: {
                    url: "{gpii.solutionsRegistry}.options.solutionsRegistryUrl",
                    writable: true,
                    termMap: {
                        id: "%id"
                    }
                }
            },
            solutionsQuerySource: {
                type: "gpii.dataSource",
                options: {
                    url: "{gpii.solutionsRegistry}.options.solutionsQueryUrl",
                    termMap: {
                        os: "%os",
                        version: "%version"
                    }
                }
            }
        }
    });

    fluid.demands("gpii.urlExpander", ["solutionsRegistry.development", "gpii.solutionsRegistry"], {
        options: {
            vars: {
                root: path.join(__dirname, "..")
            }
        }
    });

    fluid.demands("solutionsSource", "gpii.solutionsRegistry", {
        funcName: "gpii.dataSource.URL"
    });
    fluid.demands("solutionsQuerySource", "gpii.solutionsRegistry", {
        funcName: "gpii.dataSource.URL"
    });
    
})();