(function () {

    "use strict";

    var fluid = require("infusion"),
        path = require("path"),
        gpii = fluid.registerNamespace("gpii");

    fluid.require("gpiiFramework", require);
    fluid.require("./solutionGet.js", require);
    fluid.require("./solutionPost.js", require);
    fluid.require("./solutionQuery.js", require);
    
    fluid.defaults("gpii.solutionsRegistry", {
        gradeNames: ["gpii.app", "autoInit"],
        handlers: {
            solutionGet: {
                route: "/solution/:id",
                type: "get"
            },
            solutionPost: {
                route: "/solution/:id",
                type: "post"
            },
            solutionQuery: {
                route: "/solution?:query",
                type: "get"
            }
        },
        solutionRegistryUrl: "",
        solutionQueryUrl: "",
        components: {
            solutionSource: {
                type: "gpii.dataSource",
                options: {
                    url: "{gpii.solutionsRegistry}.options.solutionRegistryUrl",
                    writable: true,
                    termMap: {
                        id: "%id"
                    }
                }
            },
            solutionQuerySource: {
                type: "gpii.dataSource",
                options: {
                    url: "{gpii.solutionsRegistry}.options.solutionQueryUrl",
                    termMap: {
                        os: "%os"
                    }
                }
            }
        }
    });

    fluid.demands("gpii.urlExpander", ["solutionsRegistry.development", "gpii.solutionsRegistry"], {
        options: {
            vars: {
                port: "{gpii.server}.options.port",
                root: path.join(__dirname, "..")
            }
        }
    });

    fluid.demands("solutionSource", "gpii.solutionsRegistry", {
        funcName: "gpii.dataSource.URL"
    });
    fluid.demands("solutionQuerySource", "gpii.solutionsRegistry", {
        funcName: "gpii.dataSource.URL"
    });
    
})();