(function () {

    "use strict";

    var fluid = require("infusion"),
        path = require("path"),
        gpii = fluid.registerNamespace("gpii");

    fluid.require("kettle", require);
    fluid.require("./solutionGet.js", require);
    fluid.require("./solutionPost.js", require);
    fluid.require("./solutionQuery.js", require);

    fluid.defaults("gpii.solutionsRegistry", {
        gradeNames: ["kettle.app", "autoInit"],
        handlers: {
            solutionGet: {
                route: "/solution/:id",
                type: "get"
            },
            solutionPost: {
                route: "/solution/:id",
                type: "post"
            },
            solutionQuery: {
                route: "/solution?:query",
                type: "get"
            }
        },
        solutionRegistryUrl: "",
        solutionQueryUrl: "",
        root: path.join(__dirname, ".."),
        components: {
            rawSolutionSource: {
                type: "kettle.dataSource",
                options: {
                    url: "{gpii.solutionsRegistry}.options.solutionRegistryUrl",
                    writable: true,
                    termMap: {
                        id: "%id"
                    }
                }
            },
            rawSolutionQuerySource: {
                type: "kettle.dataSource",
                options: {
                    url: "{gpii.solutionsRegistry}.options.solutionQueryUrl",
                    termMap: {
                        os: "%os"
                    }
                }
            },
            solutionSource: {
                type: "kettle.callbackWrappingDataSource"
            },
            solutionQuerySource: {
                type: "kettle.callbackWrappingDataSource"
            }
        }
    });

})();