"use strict";

var fluid = require("infusion"),
    path = require("path"),
    gpii = fluid.registerNamespace("gpii"),
    jqUnit = fluid.registerNamespace("jqUnit"),
    kettle = fluid.registerNamespace("kettle");

fluid.registerNamespace("gpii.test.cloudBased");

gpii.test.cloudBased.FMRequestListenerMaker = function (expected) {
    return function (data) {
        jqUnit.assertDeepEq("Checking the returned data from the flowmanager: ",
            JSON.parse(data), expected);
    };
};

fluid.defaults("gpii.test.cloudBased.testCaseHolder", {
    gradeNames: ["kettle.test.testCaseHolder", "autoInit"],
    members: {
        settingsStore: {} // TODO: unused?
    },
    components: {
        fmrequest: {
            type: "kettle.test.request.http",
            options: {
                requestOptions: {
                    path: "/%userToken/settings/%appinfo",
                    port: 8081
                },
                termMap: {
                    userToken: "{testCaseHolder}.options.userToken",
                    appinfo: "{testCaseHolder}.options.appinfo"
                }
            }
        }
    }
});

gpii.test.cloudBased.gpiiConfig = function (baseDir) {
    return {
        configName: "cloudBasedFlowManager",
        configPath: path.resolve(baseDir, "./configs")
    };
};

gpii.test.cloudBased.buildTestFixture = function (testDef, baseDir) {
    testDef.expect = 1;
    testDef.sequence = fluid.makeArray(testDef.sequence);
    testDef.gradeNames = fluid.makeArray(testDef.gradeNames);
    testDef.gradeNames.push("gpii.test.cloudBased.testCaseHolder");

    testDef.sequence.unshift({
        func: "{fmrequest}.send"
    }, {
        event: "{fmrequest}.events.onComplete",
        listenerMaker: "gpii.test.cloudBased.FMRequestListenerMaker",
        makerArgs: ["{testCaseHolder}.options.expected"]
    });
    testDef.config = gpii.test.cloudBased.gpiiConfig(baseDir);

    return testDef;
};

gpii.test.cloudBased.bootstrap = function (testDefs, baseDir) {
    return kettle.test.bootstrapServer(testDefs, function (testDef) {
        return gpii.test.cloudBased.buildTestFixture(testDef, baseDir);
    });
};
