        event: "{getAuthorizedServicesRequest}.events.onComplete",
        listener: "gpii.test.cloudBased.oauth2.verifyAuthorizedServicesResponse",
        args: ["{getAuthorizedServicesRequest}", "{arguments}.0", "{testCaseHolder}.options.client_id"]
